import  React, { useState } from 'react'
import './ExpenseItem.css'
import Card from '../UI/Card'
import DataDespesa from './Data'
import DespesaItem from './DespesaItem'


function Despesa(props){

    const [desc, setDesc] = useState(props.despesas[0].descricao);


    const clickHandle = (id=null) =>{
        setDesc("Atualizado")
    }

    return (
        <Card className="despesas" >
            {props.despesas.map((despesa, i) => (
            //    console.log(despesa);
                    <div className="expense-item" key = {i}>
                        <DataDespesa data={despesa.data}/>
                        <div className="expense-item__description">
                            <h2>{desc}</h2>
                            <div className="expense-item__price">{despesa.preco}$00</div>
                        <button onClick={clickHandle} className="edit">Edit</button>
                        </div>
                    </div>

            ))};
        </Card>
    )
}

export default Despesa
